    , person.alt_email_address
    , person.preferred_contact_mode
    , 'SUBGROUP' as class_type
    , person.field1
    , person.field2
    , person.field3
    , person.field4
    , person.field5
    , person.note_collection_id
    , company.company_code
    , company.allow_aus_approval_flag
